'            CUST_FIRST_NAME,',
'            CUST_LAST_NAME,',
'            CUST_STREET_ADDRESS1,',
'            CUST_STREET_ADDRESS2,',
'            CUST_CITY,',
'            CUST_STATE,',
'            CUST_POSTAL_CODE,',
'            CUST_EMAIL,',
'            PHONE_NUMBER1,',
'            PHONE_NUMBER2,',
'            URL,',
'            CREDIT_LIMIT,',
'            TAGS)',
'          values (',
'            :P216_CUST_FIRST_NAME,',
'            :P216_CUST_LAST_NAME,',
'            :P216_CUST_STREET_ADDRESS1,',
'            :P216_CUST_STREET_ADDRESS2,',
'            :P216_CUST_CITY,',
'            :P216_CUST_STATE,',
'            :P216_CUST_POSTAL_CODE,',
'            :P216_CUST_EMAIL,',
'            :P216_PHONE_NUMBER1,',
'            :P216_PHONE_NUMBER2,',
'            :P216_URL,',
'            :P216_CREDIT_LIMIT,',
'            :P216_TAGS)',
'          returning customer_id into l_customer_id;',
'',
'          :P216_CUSTOMER_ID := l_customer_id;',
'    end if;',
'',
'    -- Insert a row into the Order Header table',
